import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Orders.css';

const Orders = () => {
  const location = useLocation();
  const { deliveryDetails, cart } = location.state || { deliveryDetails: {}, cart: [] };

  console.log('Received state in Orders page:', location.state);

  console.log('Navigating to orders page with:', { deliveryDetails, cart });
  navigate('/orders', { state: { deliveryDetails, cart } });

  const handlePopupSubmit = (e) => {
    e.preventDefault();
    setOrderPlaced(true);
    setShowPopup(false);
    setTimeout(() => {
      navigate('/orders', { state: { deliveryDetails, cart } }); // Ensure this line is correct
    }, 2000); // Redirect after 2 seconds
  };

  return (
    <div className="orders-container">
      <h2>Your Orders</h2>
      {cart.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <>
          <div className="delivery-details">
            <h3>Delivery Details</h3>
            <p><strong>Name:</strong> {deliveryDetails.name || 'N/A'}</p>
            <p><strong>Phone:</strong> {deliveryDetails.phone || 'N/A'}</p>
            <p><strong>Address:</strong> {deliveryDetails.address || 'N/A'}</p>
          </div>
          <ul>
            {cart.map((item, index) => (
              <li key={index}>
                {item.name} - ₹{item.price} x {item.quantity} = ₹{item.price * item.quantity}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default Orders;
