import React, { useContext, useState } from 'react'; 
import { CartContext } from '../context/CartContext';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { QRCodeCanvas } from 'qrcode.react';
import { createClient } from '@supabase/supabase-js';
import axios from 'axios';
import './Invoice.css';

const supabase = createClient(
  'https://cslnkpnxwqahipwrjqna.supabase.co',
  'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzIiwicmVmIjoiY3Nsbmtwbnh3cWFoaXB3cmpxbmEiLCJyb2xlIjoiYW5vbiIsImlhdCI6MTczOTYzMDE0OSwiZXhwIjoyMDU1MjA2MTQ5fQ.jqJ9wbyVFx09RvlNXnLZipCzFvjY2RTfcbO4XoiTfU8'
);

const Invoice = () => {
  const { cart } = useContext(CartContext);
  const [showQR, setShowQR] = useState(false);
  const [saving, setSaving] = useState(false);
  const [showSMSInput, setShowSMSInput] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');

  const totalAmount = cart.reduce((total, item) => total + item.price * item.quantity, 0);

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text('Velavan Super Stores - Invoice', 10, 10);
    doc.autoTable({
      head: [['Item', 'Price', 'Quantity', 'Total']],
      body: cart.map(item => [item.name, `₹${item.price}`, item.quantity, `₹${item.price * item.quantity}`]),
    });
    doc.text(`Total Amount: ₹${totalAmount}`, 10, doc.lastAutoTable.finalY + 10);
    doc.save('invoice.pdf');
  };

  const generateQRCodeValue = () => {
    return `Velavan Super Stores\nTotal: ₹${totalAmount}\n${cart.map(item => `${item.name}: ₹${item.price} x ${item.quantity}`).join('\n')}`;
  };

  const handleSendSMS = async () => {
    if (!phoneNumber) {
      alert('Please enter a phone number');
      return;
    }

    const message = `Your purchase details:\n${cart.map(item => `${item.name} x ${item.quantity} = ₹${item.price * item.quantity}`).join('\n')}\nTotal Amount: ₹${totalAmount}`;
    
    try {
      await axios.post('https://api.twilio.com/2010-04-01/Accounts/AC11bdfa290a8300b120cf299eebd540cf/Messages.json',
        new URLSearchParams({
          To: phoneNumber,
          From: '+YOUR_TWILIO_NUMBER',
          Body: message
        }),
        {
          auth: {
            username: 'AC11bdfa290a8300b120cf299eebd540cf',
            password: '6979edaf3225e278358c003e7a9a7bc9'
          }
        }
      );
      alert('SMS sent successfully!');
    } catch (error) {
      console.error('Error sending SMS:', error);
      alert('Failed to send SMS.');
    }
  };

  return (
    <div className="invoice-container">
      <h2>Invoice</h2>
      {cart.length === 0 ? (
        <p>No items in the cart</p>
      ) : (
        <div>
          <h3>Velavan Super Stores</h3>
          <ul>
            {cart.map((item, index) => (
              <li key={index}>{item.name} - ₹{item.price} x {item.quantity} = ₹{item.price * item.quantity}</li>
            ))}
          </ul>
          <h3>Total Amount: ₹{totalAmount}</h3>
          
          <button onClick={generatePDF}>Generate PDF</button>
          <button onClick={() => setShowQR(!showQR)}>Generate QR Code</button>
          {showQR && <QRCodeCanvas value={generateQRCodeValue()} />}
          
          <button onClick={() => setShowSMSInput(!showSMSInput)}>Send SMS</button>
          {showSMSInput && (
            <div>
              <input
                type="text"
                placeholder="Enter phone number"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
              />
              <button onClick={handleSendSMS}>Send</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Invoice;
