import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { QRCodeCanvas } from 'qrcode.react';
import { createClient } from '@supabase/supabase-js';
import './Invoice.css';

const supabase = createClient(
  'https://cslnkpnxwqahipwrjqna.supabase.co',
  'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImNzbG5rcG54d3FhaGlwd3JqcW5hIiwicm9sZSI6ImFub24iLCJpYXQiOjE3Mzk2MzAxNDksImV4cCI6MjA1NTIwNjE0OX0.jqJ9wbyVFx09RvlNXnLZipCzFvjY2RTfcbO4XoiTfU8'
);

const Invoice = () => {
  const { cart } = useContext(CartContext);
  const [showQR, setShowQR] = useState(false);
  const [saving, setSaving] = useState(false);
  const [showSMS, setShowSMS] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');

  const totalAmount = cart.reduce((total, item) => total + item.price * item.quantity, 0);

  const saveInvoiceToSupabase = async () => {
    setSaving(true);

    const date = new Date().toISOString();

    const { data, error } = await supabase.from('invoices').insert([
      {
        date,
        items: cart.map(item => ({
          name: item.name,
          price: item.price,
          quantity: item.quantity,
          total: item.price * item.quantity
        })),
        total_amount: totalAmount
      }
    ]);

    setSaving(false);

    if (error) {
      console.error('Error saving invoice:', error);
      alert('Failed to save invoice. Please try again.');
    } else {
      console.log('Invoice saved successfully:', data);
      alert('Invoice saved successfully!');
    }
  };

  const handleDownloadPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text('Velavan Super Stores', doc.internal.pageSize.getWidth() / 2, 22, { align: 'center' });
    doc.setFontSize(12);
    doc.text('Phone: 9047665999', 14, 30);
    doc.text('Email: velavansuperstores@gmail.com', 14, 36);
    doc.text('GST No: 22AAXFV7324B1Z0', 14, 42);
    doc.text('Location: Bhavani main road, Salangapalayam, Tamil Nadu-638455', 14, 48);

    const date = new Date();
    doc.text(`Date: ${date.toLocaleDateString()}`, doc.internal.pageSize.getWidth() - 14, 30, { align: 'right' });
    doc.text(`Time: ${date.toLocaleTimeString()}`, doc.internal.pageSize.getWidth() - 14, 36, { align: 'right' });

    doc.line(14, 50, 200, 50);

    const tableColumn = ['Product', 'Price', 'Quantity', 'Total'];
    const tableRows = [];

    cart.forEach(item => {
      tableRows.push([item.name, `₹${item.price}`, item.quantity, `₹${item.price * item.quantity}`]);
    });

    doc.autoTable({ head: [tableColumn], body: tableRows, startY: 60 });

    doc.setFontSize(14);
    doc.text(`Total Amount: ₹${totalAmount}`, 14, doc.autoTable.previous.finalY + 10);

    doc.setLineWidth(0.5);
    doc.rect(10, 10, 190, doc.autoTable.previous.finalY + 20);

    doc.save('invoice.pdf');
  };

  const sendSMS = async () => {
    if (!phoneNumber.trim()) {
      alert('Please enter a phone number.');
      return;
    }

    const message = `Your purchase details:\n` + 
                    cart.map(item => `${item.name}: ₹${item.price} x ${item.quantity} = ₹${item.price * item.quantity}`).join('\n') + 
                    `\nTotal: ₹${totalAmount}`;

    try {
      // Replace with actual SMS API request (e.g., Twilio, Fast2SMS)
      console.log(`Sending SMS to ${phoneNumber}: ${message}`);
      alert(`SMS sent to ${phoneNumber}!`);
    } catch (error) {
      console.error('Error sending SMS:', error);
      alert('Failed to send SMS. Please try again.');
    }
  };

  return (
    <div className="invoice-container">
      <h2>Invoice</h2>
      {cart.length === 0 ? (
        <p>No items in the cart</p>
      ) : (
        <div>
          <div className="invoice-content">
            <h3>Velavan Super Stores</h3>
            <p>Phone: 9047665999</p>
            <p>Email: velavansuperstores@gmail.com</p>
            <p>GST No: 22AAXFV7324B1Z0</p>
            <p>Location: Bhavani main road, Salangapalayam, Tamil Nadu-638455</p>
            <hr />
            <ul>
              {cart.map((item, index) => (
                <li key={index}>
                  {item.name} - ₹{item.price} x {item.quantity} = ₹{item.price * item.quantity}
                </li>
              ))}
            </ul>
            <h3>Total Amount: ₹{totalAmount}</h3>
          </div>
          <button onClick={handleDownloadPDF}>Download PDF</button>
          <button 
            onClick={saveInvoiceToSupabase} 
            disabled={saving} 
            style={{ marginLeft: '10px' }}
          >
            {saving ? 'Saving...' : 'Save'}
          </button>
          <button onClick={() => setShowQR(true)} style={{ marginLeft: '10px' }}>Generate Payment</button>
          <button onClick={() => setShowSMS(!showSMS)} style={{ marginLeft: '10px' }}>
            {showSMS ? 'Hide SMS' : 'Send SMS'}
          </button>

          {showQR && (
            <div className="qr-container">
              <h3>Scan to Pay</h3>
              <QRCodeCanvas
                value={`upi://pay?pa=sabarivasan1239@okhdfcbank&pn=Velavan%20Super%20Stores&am=${totalAmount}&cu=INR`}
                size={200}
              />
              <p>UPI ID: sabarivasan1239@okhdfcbank</p>
            </div>
          )}

          {showSMS && (
            <div className="sms-container">
              <input
                type="text"
                placeholder="Enter phone number"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                style={{ marginTop: '10px', padding: '5px', width: '200px' }}
              />
              <button onClick={sendSMS} style={{ marginLeft: '10px' }}>Send</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Invoice;
