import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        <Link to="/" className="logo-link" onClick={closeMenu}>
          <h2 className="logo">
            <span className="logo-icon">ðŸ›’</span>
            Velavan Super Stores
          </h2>
        </Link>

        {/* Mobile Menu Button */}
        <div className={`mobile-menu-btn ${menuOpen ? 'active' : ''}`} onClick={toggleMenu}>
          <div className="menu-bar"></div>
          <div className="menu-bar"></div>
          <div className="menu-bar"></div>
        </div>

        {/* Navigation Menu */}
        <ul className={`nav-menu ${menuOpen ? 'active' : ''}`}>
          <li>
            <Link to="/" className="nav-link" onClick={closeMenu}>
              Home
            </Link>
          </li>
          <li>
            <Link to="/products" className="nav-link" onClick={closeMenu}>
              Products
            </Link>
          </li>
          <li>
            <Link to="/cart" className="nav-link" onClick={closeMenu}>
              Cart
            </Link>
          </li>
          <li>
            <Link to="/invoice" className="nav-link" onClick={closeMenu}>
              Invoice
            </Link>
          </li>
          <li>
            <Link to="/orders" className="nav-link" onClick={closeMenu}>
              Orders
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
