  const [paymentSuccess, setPaymentSuccess] = useState(false); // Track payment
  const navigate = useNavigate();

  useEffect(() => {
    setIsVisible(true);
  }, []);

  // ... keep totalAmount and handleDownloadPDF

  const handleRazorpayPayment = () => {
