      key: "rzp_test_cmLeqKg61pFBgh",
      amount: totalAmount * 100,
      currency: "INR",
      name: "Velavan Super Stores",
      description: "Payment for your purchase",
      image: "/logo.png",
      handler: function (response) {
        alert("Payment successful! Payment ID: " + response.razorpay_payment_id);
        setPaymentSuccess(true); // Set payment as done
      },
      prefill: {
        name: "Customer Name",
        email: "customer@example.com",
        contact: "9999999999",
      },
      theme: {
        color: "#3399cc",
      },
    };

    const rzp1 = new window.Razorpay(options);
    rzp1.open();
  };

  const handleProceedToCheckout = () => {
    navigate("/checkout");
  };

  return (
    <div className={`invoice-container ${isVisible ? 'visible' : ''}`}>
      {/* ...existing code... */}
      {cart.length !== 0 && (
        <div className="action-buttons">
          <button className="action-btn download-btn" onClick={handleDownloadPDF}>
            Download PDF
          </button>

          <button className="action-btn payment-btn" onClick={handleRazorpayPayment}>
            Pay with Razorpay
          </button>

          {paymentSuccess && (
            <button className="action-btn checkout-btn" onClick={handleProceedToCheckout}>
              Proceed to Checkout
            </button>
          )}
        </div>
      )}
    </div>
  );
};
