import React, { useState, useContext } from 'react';
import { CartContext } from '../context/CartContext'; 
import Product from "./components/Products"; 

const productsList = [
  { "id": 1, "name": "Dove Soap", "price": 30, "category": "Soap", "description": "A gentle soap for daily use." },
  { "id": 2, "name": "Lux Soap", "price": 25, "category": "Soap", "description": "A luxurious soap for smooth skin." },
  { "id": 3, "name": "Pears Soap", "price": 45, "category": "Soap", "description": "A transparent glycerin soap for soft skin." },

  { "id": 4, "name": "Clinic Plus Shampoo", "price": 110, "category": "Shampoo", "description": "A protein-rich shampoo for healthy hair." },
  { "id": 5, "name": "Dove Shampoo", "price": 180, "category": "Shampoo", "description": "A nourishing shampoo for soft hair." },
  { "id": 6, "name": "Sunsilk Shampoo", "price": 150, "category": "Shampoo", "description": "A smoothening shampoo for silky hair." },

  { "id": 7, "name": "Bru Instant Coffee", "price": 160, "category": "Coffee", "description": "A strong instant coffee blend." },
  { "id": 8, "name": "Nescafe Classic Coffee", "price": 220, "category": "Coffee", "description": "A rich and aromatic instant coffee." },
  { "id": 9, "name": "Narasu's Filter Coffee", "price": 180, "category": "Coffee", "description": "A popular South Indian filter coffee." },

  { "id": 10, "name": "Tata Tea Gold", "price": 130, "category": "Tea", "description": "A strong and flavorful tea." },
  { "id": 11, "name": "Lipton Green Tea", "price": 180, "category": "Tea", "description": "A healthy green tea alternative." },
  { "id": 12, "name": "Red Label Tea", "price": 110, "category": "Tea", "description": "A widely used strong tea blend." },

  { "id": 13, "name": "Cadbury Dairy Milk", "price": 50, "category": "Chocolate", "description": "A creamy milk chocolate." },
  { "id": 14, "name": "Nestle KitKat", "price": 40, "category": "Chocolate", "description": "A crispy wafer chocolate." },
  { "id": 15, "name": "5 Star Chocolate", "price": 35, "category": "Chocolate", "description": "A chewy caramel chocolate." },

  { "id": 16, "name": "GRB Ghee", "price": 550, "category": "Ghee", "description": "A pure and aromatic ghee." },
  { "id": 17, "name": "Amul Ghee", "price": 520, "category": "Ghee", "description": "A premium quality ghee." },
  { "id": 18, "name": "Aavin Ghee", "price": 500, "category": "Ghee", "description": "A locally preferred dairy ghee." },

  { "id": 19, "name": "Idhayam Gingelly Oil", "price": 260, "category": "Oil", "description": "A popular sesame oil brand in Tamil Nadu." },
  { "id": 20, "name": "Gold Winner Sunflower Oil", "price": 170, "category": "Oil", "description": "A widely used cooking oil in Tamil Nadu." },
  { "id": 21, "name": "Fortune Sunflower Oil", "price": 160, "category": "Oil", "description": "A refined and pure sunflower oil." },

  { "id": 22, "name": "Colgate Toothpaste", "price": 55, "category": "Toothpaste", "description": "A widely used fluoride toothpaste." },
  { "id": 23, "name": "Sensodyne Toothpaste", "price": 120, "category": "Toothpaste", "description": "A toothpaste for sensitive teeth." },
  { "id": 24, "name": "Close-Up Toothpaste", "price": 50, "category": "Toothpaste", "description": "A gel-based toothpaste for fresh breath." },

  { "id": 25, "name": "Britannia Good Day Biscuits", "price": 30, "category": "Biscuits", "description": "A crunchy and tasty biscuit." },
  { "id": 26, "name": "Parle-G Biscuits", "price": 20, "category": "Biscuits", "description": "A classic glucose biscuit." },
  { "id": 27, "name": "Hide & Seek Fab Biscuits", "price": 35, "category": "Biscuits", "description": "A choco-filled biscuit delight." },

  { "id": 28, "name": "Urid Dal", "price": 120, "category": "Pulses", "description": "A commonly used dal in South Indian dishes." },
  { "id": 29, "name": "Toor Dal", "price": 150, "category": "Pulses", "description": "A nutritious lentil used in sambars." },
  { "id": 30, "name": "Moong Dal", "price": 140, "category": "Pulses", "description": "A light and easily digestible dal." },

  { "id": 31, "name": "Everest Chilli Powder", "price": 60, "category": "Spices", "description": "A vibrant red chili powder for spicy dishes." },
  { "id": 32, "name": "MDH Garam Masala", "price": 80, "category": "Spices", "description": "A blend of aromatic spices for Indian curries." },
  { "id": 33, "name": "Everest Turmeric Powder", "price": 50, "category": "Spices", "description": "A pure turmeric powder for healthy cooking." },
  { "id": 34, "name": "Sakthi Coriander Powder", "price": 45, "category": "Spices", "description": "A fresh coriander powder for flavorful dishes." },
  { "id": 35, "name": "Everest Cumin Powder", "price": 55, "category": "Spices", "description": "A strong cumin powder for rich taste." }

];

const Products = () => {
  const { addToCart } = useContext(CartContext);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('All');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  const filteredProducts = productsList.filter(product => {
    return (category === 'All' || product.category === category) && product.name.toLowerCase().includes(search.toLowerCase());
  });

  const handleAddToCart = (product) => {
    addToCart(product, quantity);
    setSelectedProduct(null);
    setQuantity(1);
  };

  return (
    <div className="products-container">
      <h2>Products</h2>
      <div className="filter-container">
        <input 
          type="text" 
          placeholder="Search products..." 
          value={search} 
          onChange={(e) => setSearch(e.target.value)} 
        />
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="All">All Categories</option>
          <option value="Soap">Soap</option>
          <option value="Shampoo">Shampoo</option>
          <option value="Coffee">Coffee</option>
          <option value="Tea">Tea</option>
          <option value="Chocolate">Chocolate</option>
          <option value="Ghee">Ghee</option>
          <option value="Oil">Oil</option>
          <option value="Toothpaste">Toothpaste</option>
          <option value="Biscuits">Biscuits</option>
          <option value="Pulses">Pulses</option>
          <option value="Spices">Spices</option>
          
          
          

          
          
          
          

          {/* Add more categories as needed */}
        </select>
      </div>
      <div className="products-grid">
        {filteredProducts.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <p>Price: ${product.price}</p>
            <button onClick={() => setSelectedProduct(product)}>View Details</button>
          </div>
        ))}
      </div>
      {selectedProduct && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setSelectedProduct(null)}>&times;</span>
            <h2>{selectedProduct.name}</h2>
            <p>{selectedProduct.description}</p>
            <p>Price: ${selectedProduct.price}</p>
            <input 
              type="number" 
              value={quantity} 
              min="1" 
              onChange={(e) => setQuantity(parseInt(e.target.value))} 
            />
            <button onClick={() => handleAddToCart(selectedProduct)}>Add to Cart</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Products;
