import React, { useContext, useState, useEffect } from 'react';
import { CartContext } from '../context/CartContext';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { createClient } from '@supabase/supabase-js';
import './Invoice.css';

const supabase = createClient(
  'https://cslnkpnxwqahipwrjqna.supabase.co',
  'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9...'
);

const Invoice = () => {
  const { cart } = useContext(CartContext);
  const [saving, setSaving] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [showOrderPopup, setShowOrderPopup] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const totalAmount = cart.reduce((total, item) => {
    const price = Number(item.price) || 0;
    const quantity = Number(item.quantity) || 0;
    return total + (price * quantity);
  }, 0);

  const saveInvoiceToSupabase = async () => {
    setSaving(true);

    const date = new Date().toISOString();
    const invoiceData = {
      date,
      items: cart.map(item => ({
        name: item.name,
        price: Number(item.price) || 0,
        quantity: Number(item.quantity) || 0,
        total: (Number(item.price) || 0) * (Number(item.quantity) || 0)
      })),
      total_amount: totalAmount
    };

    const { error } = await supabase.from('invoices').insert([invoiceData]);
    setSaving(false);

    if (error) {
      console.error('Supabase Error:', error);
      alert(`Failed to save invoice: ${error.message}`);
    } else {
      setShowOrderPopup(true);
      setTimeout(() => setShowOrderPopup(false), 3000);
    }
  };

  const handleDownloadPDF = () => {
    const doc = new jsPDF();
    // ... PDF generation code with number checks ...
  };

  const handleRazorpayPayment = () => {
    // ... payment handling code ...
  };

  return (
    <div className={`invoice-container ${isVisible ? 'visible' : ''}`}>
      {/* ... rest of the Invoice component ... */}
    </div>
  );
};

export default Invoice;
