import React, { useState, useContext, useEffect } from 'react';
import { CartContext } from '../context/CartContext';
import { supabase } from '../path-to-your-supabase-client'; // Update the path accordingly
import './Products.css';

const Products = () => {
  const { addToCart } = useContext(CartContext);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('All');
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [sortOption, setSortOption] = useState('default');
  const [productsList, setProductsList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch products from Supabase
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase
          .from('products')
          .select('*');
        
        if (error) throw error;
        
        setProductsList(data);
      } catch (err) {
        setError(err.message);
        console.error('Error fetching products:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const filteredProducts = productsList.filter(product => {
    return (category === 'All' || product.category === category) && 
           product.name.toLowerCase().includes(search.toLowerCase());
  });

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    switch(sortOption) {
      case 'price-low':
        return a.price - b.price;
      case 'price-high':
        return b.price - a.price;
      case 'name-asc':
        return a.name.localeCompare(b.name);
      case 'name-desc':
        return b.name.localeCompare(a.name);
      default:
        return 0;
    }
  });

  const categories = [...new Set(productsList.map(product => product.category))];

  const handleAddToCart = (product) => {
    addToCart(product, quantity);
    setSelectedProduct(null);
    setQuantity(1);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading products...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p>Error loading products: {error}</p>
        <button onClick={() => window.location.reload()}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="products-page">
      <div className="products-header">
        <h1 className="page-title">Our Products</h1>
        <p className="page-subtitle">Find everything you need for your daily life</p>
      </div>

      <div className="products-controls">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search products..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="search-input"
          />
          <span className="search-icon">üîç</span>
        </div>

        <div className="filter-group">
          <select 
            value={category} 
            onChange={(e) => setCategory(e.target.value)}
            className="filter-select"
          >
            <option value="All">All Categories</option>
            {categories.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <select 
            value={sortOption} 
            onChange={(e) => setSortOption(e.target.value)}
            className="filter-select"
          >
            <option value="default">Sort by</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
            <option value="name-asc">Name: A-Z</option>
            <option value="name-desc">Name: Z-A</option>
          </select>
        </div>
      </div>

      {sortedProducts.length === 0 ? (
        <div className="no-products">
          <img src="https://cdn.dribbble.com/users/2382015/screenshots/6065978/no_result.gif" alt="No products found" />
          <p>No products match your search criteria</p>
        </div>
      ) : (
        <div className="products-grid">
          {sortedProducts.map((product) => (
            <div key={product.id} className="product-card">
              <div className="product-image-container">
                <img
                  src={product.image}
                  alt={product.name}
                  className="product-image"
                  onClick={() => setSelectedProduct(product)}
                />
              </div>
              <div className="product-info">
                <h3 className="product-name">{product.name}</h3>
                <p className="product-category">{product.category}</p>
                <p className="product-price">‚Çπ{product.price}</p>
                <button 
                  className="view-details-btn"
                  onClick={() => setSelectedProduct(product)}
                >
                  View Details
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {selectedProduct && (
        <div className="product-modal">
          <div className="modal-overlay" onClick={() => setSelectedProduct(null)}></div>
          <div className="modal-content">
            <button 
              className="close-modal"
              onClick={() => setSelectedProduct(null)}
            >
              &times;
            </button>
            
            <div className="modal-product-image">
              <img src={selectedProduct.image} alt={selectedProduct.name} />
            </div>
            
            <div className="modal-product-details">
              <h2>{selectedProduct.name}</h2>
              <p className="modal-category">{selectedProduct.category}</p>
              <p className="modal-description">{selectedProduct.description}</p>
              
              <div className="price-container">
                <span className="modal-price">‚Çπ{selectedProduct.price}</span>
              </div>
              
              <div className="quantity-controls">
                <button 
                  className="quantity-btn minus"
                  onClick={() => setQuantity(prev => Math.max(1, prev - 1))}
                >
                  ‚àí
                </button>
                <span className="quantity-value">{quantity}</span>
                <button 
                  className="quantity-btn plus"
                  onClick={() => setQuantity(prev => prev + 1)}
                >
                  +
                </button>
              </div>
              
              <button 
                className="add-to-cart-btn"
                onClick={() => handleAddToCart(selectedProduct)}
              >
                Add to Cart - ‚Çπ{(selectedProduct.price * quantity).toFixed(2)}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Products;
