import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { QRCodeCanvas } from 'qrcode.react';
import { createClient } from '@supabase/supabase-js';
import axios from 'axios';
import './Invoice.css';

// ✅ Supabase Client Setup
const supabase = createClient(
  'https://cslnkpnxwqahipwrjqna.supabase.co',
  'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImNzbG5rcG54d3FhaGlwd3JqcW5hIiwicm9sZSI6ImFub24iLCJpYXQiOjE3Mzk2MzAxNDksImV4cCI6MjA1NTIwNjE0OX0.jqJ9wbyVFx09RvlNXnLZipCzFvjY2RTfcbO4XoiTfU8'
);

const Invoice = () => {
  const { cart } = useContext(CartContext);
  const [showQR, setShowQR] = useState(false);
  const [saving, setSaving] = useState(false);
  const [showSMSInput, setShowSMSInput] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState('');

  const totalAmount = cart.reduce((total, item) => total + item.price * item.quantity, 0);

  // ✅ Function to Send SMS via TXTAPI
  const sendSMS = async () => {
    if (!phoneNumber.trim()) {
      alert('Please enter a valid phone number.');
      return;
    }

    // Ensure the phone number is in the correct format
    const formattedPhoneNumber = phoneNumber.startsWith('+') ? phoneNumber : `+91${phoneNumber}`;

    // ✅ Invoice Message
    const message = `Your purchase details:\n` + 
                    cart.map(item => `${item.name}: ₹${item.price} x ${item.quantity} = ₹${item.price * item.quantity}`).join('\n') + 
                    `\nTotal: ₹${totalAmount}`;

    try {
      // ✅ TXTAPI Request
      const response = await axios.post('https://txtapi.com/send', {  // Ensure this is the correct API URL
        key: '',  // Your API Key
        number: formattedPhoneNumber,
        message: message,
        sender_id: 'VELAVAN'  // Update based on TXTAPI requirements
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log('SMS Response:', response.data);

      if (response.data.success) {
        alert(`SMS sent to ${formattedPhoneNumber}!`);
      } else {
        alert('Failed to send SMS. Please try again.');
      }
    } catch (error) {
      console.error('Error sending SMS:', error.response ? error.response.data : error.message);
      alert('Failed to send SMS. Please check the API key, phone number format, or API endpoint.');
    }
  };

  return (
    <div className="invoice-container">
      <h2>Invoice</h2>
      {cart.length === 0 ? (
        <p>No items in the cart</p>
      ) : (
        <div>
          <div className="invoice-content">
            <h3>Velavan Super Stores</h3>
            <p>Phone: 9047665999</p>
            <p>Email: velavansuperstores@gmail.com</p>
            <p>GST No: 22AAXFV7324B1Z0</p>
            <p>Location: Bhavani main road, Salangapalayam, Tamil Nadu-638455</p>
            <hr />
            <ul>
              {cart.map((item, index) => (
                <li key={index}>
                  {item.name} - ₹{item.price} x {item.quantity} = ₹{item.price * item.quantity}
                </li>
              ))}
            </ul>
            <h3>Total Amount: ₹{totalAmount}</h3>
          </div>

          {/* ✅ SMS Button */}
          <button onClick={() => setShowSMSInput(!showSMSInput)} style={{ marginTop: '10px' }}>
            Send SMS
          </button>

          {showSMSInput && (
            <div className="sms-input">
              <input
                type="text"
                placeholder="Enter phone number"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
              />
              <button onClick={sendSMS}>Send</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Invoice;
