import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminLogin.css";

const AdminLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loginType, setLoginType] = useState("admin"); // "admin" or "stock"
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (loginType === "admin") {
      if (email === "admin@velavan.com" && password === "admin123") {
        navigate("/home"); // Redirect to Admin Home Page
      } else {
        setError("Invalid Admin Email or Password");
      }
    } else {
      if (email === "stock@velavan.com" && password === "stock123") {
        navigate("/stock"); // Redirect to Stock Management Page
      } else {
        setError("Invalid Stock Manager Email or Password");
      }
    }
  };

  return (
    <div className="container">
      <h2>{loginType === "admin" ? "Admin Login" : "Stock Management Login"}</h2>
      <div className="toggle-buttons">
        <button
          className={loginType === "admin" ? "active" : ""}
          onClick={() => setLoginType("admin")}
        >
          Admin Login
        </button>
        <button
          className={loginType === "stock" ? "active" : ""}
          onClick={() => setLoginType("stock")}
        >
          Stock Management Login
        </button>
      </div>
      <form className="form" onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Enter your email"
          className="input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Enter your password"
          className="input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {error && <p className="error">{error}</p>}
        <button type="submit" className="button">Login</button>
      </form>
    </div>
  );
};

export default AdminLogin;
